mapper.insert:
		INSERT
		  INTO 
		  	   TB_MEMBER 
		  	   (
		  	    MEM_NO
               ,MEM_NAME
		  	   ,MEM_NICKNAME
               ,MEM_ID
		  	   ,MEM_PWD
		  	   ,MEM_EMAIL
		  	   )
		VALUES 
			   (
			    SEQ_MEM.NEXTVAL
			   ,#{ memName }
			   ,#{ memNickname }
			   ,#{ memId }
			   ,#{ memPwd }
			   ,#{ memEmail }
			   )

mapper.select:
SELECT 
	   COUNT(*)
  FROM
	   TB_MEMBER
 WHERE


	   MEM_EMAIL = #{ checkVal }


 	   MEM_ID = #{ checkVal }


 	   MEM_NICKNAME = #{ checkVal }
